const { cobalt } = require('./main.js'); // Update with the actual path to your yta function file

async function testYta() {
  const sampleUrl = 'https://www.youtube.com/watch?v=qHDJSRlNhVs'; // Example YouTube URL
  const result = await cobalt(sampleUrl);

  if (result) {
    console.log('Test Passed! Retrieved Data:');
    console.log(result);
  } else {
    console.error('Test Failed! No data returned.');
  }
}

testYta();